package cn.finder.wae.api.sdk.request;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import cn.finder.httpcommons.attri.JsonProperty;import cn.finder.httpcommons.request.DefaultRequest;import cn.finder.httpcommons.utils.JsonUtils;import cn.finder.wae.api.sdk.response.FlowTaskCompleteResponse;/*** * 任务流程完成请求 * 如果是指派 ，那么variables 里面设置 toUserId的值为userId */public class FlowTaskCompleteRequest extends DefaultRequest<FlowTaskCompleteResponse> {	@Override	public String apiName() {		return "wae.flow.task.complete";	}	@Override	public void validate() {	}	private String taskId;	public String getTaskId() {		return taskId;	}	public void setTaskId(String taskId) {		this.taskId = taskId;	}	private List<Map<String, Object>> variables = new ArrayList<Map<String, Object>>();	/***	 * 添加变量	 * 	 * @param variable	 */	public void addVariable(Map<String, Object> variable) {		variables.add(variable);	}	public void addVariable(List<Map<String, Object>> variables) {		this.variables.addAll(variables);	}	private String paramString;	@JsonProperty(name = "paramString")	public String getParamString() {		return JsonUtils.getJsonString4JavaPOJO(variables);	}	/*	 * public void setParamString(String paramString) {	 * this.paramString = paramString;	 * }	 */	private String rr_remark;	private String business_key;	private String user_id;	public String getRr_remark() {		return rr_remark;	}	public void setRr_remark(String rr_remark) {		this.rr_remark = rr_remark;	}	public String getBusiness_key() {		return business_key;	}	public void setBusiness_key(String business_key) {		this.business_key = business_key;	}	public String getUser_id() {		return user_id;	}	public void setUser_id(String user_id) {		this.user_id = user_id;	}}