package cn.finder.wae.api.sdk.response;import java.util.HashMap;import java.util.List;import java.util.Map;import cn.finder.httpcommons.attri.JsonArrayAttribute;import cn.finder.httpcommons.attri.JsonArrayItemAttribute;import cn.finder.httpcommons.response.ApiResponse;	/***	* 流程任务参数响应	*/public class FindFlowTaskVariablesResponse extends ApiResponse{	private List<Map<String,Object>> entities;	@JsonArrayAttribute(name="entities")	@JsonArrayItemAttribute(clazzType=HashMap.class)	public void setEntities(List<Map<String,Object>> entities)	{		this.entities = entities;	}	public	List<Map<String,Object>> getEntities()	{		return entities;	}		/***	 * 获取流程变量	 * @return	 */	public Map<String,Object> getVariables(){		if(entities!=null && entities.size()>0){			return entities.get(0);		}		return null;	}	}