package cn.finder.wae.api.sdk.response;import cn.finder.httpcommons.attri.JsonArrayAttribute;import cn.finder.httpcommons.attri.JsonArrayItemAttribute;import cn.finder.httpcommons.response.ApiResponse;import java.util.List;import cn.finder.wae.api.sdk.domain.User;	/***	* 获取用户信息接口响应	*/public class FindUserInfoResponse extends ApiResponse{	private List<User> entities;	@JsonArrayAttribute(name="entities")	@JsonArrayItemAttribute(clazzType=User.class)	public void setEntities(List<User> entities)	{		this.entities = entities;	}	public	List<User> getEntities()	{		return entities;	}		public User getUser(){		if(this.entities!=null&&entities.size()>0){			return entities.get(0);		}		return null;	}}