package cn.finder.wae.global.api.request;import java.util.HashMap;import java.util.Map;import cn.finder.httpcommons.attri.JsonProperty;import cn.finder.httpcommons.request.UploadRequest;import cn.finder.httpcommons.utils.FileItem;import cn.finder.wae.global.api.response.MediaUploadResponse;/**** 资源文件添加请求*/public class MediaUploadRequest extends UploadRequest<MediaUploadResponse>{	@Override	public  String apiName()	{		return	"wae.global.image.upload";	}	@Override	public  void validate()	{	}	@Override	public  Map<String, FileItem> fileParameters()	{		Map<String, FileItem> files = new HashMap<String, FileItem>();		files.put("binary_data", media_file);		return files;	}		private String name;	    		public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	private	FileItem	media_file;	public void setMedia_file(FileItem media_file)	{		 this.media_file=media_file;	}	@JsonProperty(name="binary_data")	public FileItem getMedia_file() {		return media_file;	}			}